void close_file()
{
  struct source_context *old;

  old = state.src;
  state.src = state.src->prev;
  if (old->f != NULL) {
    #ifndef __MSDOS__
      fseek(old->f, -1, SEEK_END);
      if (fgetc(old->f) != '\n') {
        char complaint[BUFSIZ];

        sprintf(complaint,
                "Missing newline before EOF in file \"%s\"",
                old->name);
        gpwarning(GPW_UNKNOWN, complaint);
      }
    #endif
    fclose(old->f);
    fclose(old->f2);
    free(old->name);
  }
  free(old);
}

